function OpenInNewTab(e){var t=window.open(e,"_blank");t.focus()}function PlayVideo(e,t,n){if(supports_video()){if(n==="unsupported"){console.log("video no");OpenInNewTab(t)}else{var r=e.split("_");var i=r[0];var s=r[2];r=i+"_thumb_"+s;var o=i+"_playBtn_"+s;document.getElementById(r).style.display="none";document.getElementById(o).style.display="none";if(n==="mp4")document.getElementById(e).play();else if(n==="youtube"){var u=document.getElementById(e).dataset.source;document.getElementById(e).src=u}document.getElementById(e).style.display="block"}}else OpenInNewTab(t)}function Open(e){window.location=e}function supports_video(){return!!document.createElement("video").canPlayType}$(document).ready(function(){$("video").bind("ended",function(){var e=this.id.split("_");var t=e[0];var n=e[2];e=t+"_thumb_"+n;var r=t+"_playBtn_"+n;$(this).hide();$("#"+e).show();$("#"+r).show()});$("video").bind("pause",function(){var e=this.id.split("_");var t=e[0];var n=e[2];e=t+"_thumb_"+n;var r=t+"_playBtn_"+n;$("#"+r).show()})})